mapper.select:
SELECT

FROM TBL_MEMBER
WHERE
	USER_ID = #{user_id}
AND
	USER_PASSWORD = #{password};

mapper.insert:
INSERT INTO TBL_MEMBER
(
	USER_ID
	, USER_PASSWORD
	, USER_NAME
	, USER_PHONE
	, USER_EMAIL
	, USER_CREATEDATE
	, USER_FAILCNT
	, USER_STATUS
	, USER_GRADE
)
VALUES (
	#{userId}
	, #{userPassword}
	, #{userName}
	, #{userPhone}
	, #{userEmail}
	, now()
	, 0
	, 1
	, 'NORMAL'
);
